package com.electric.engineering.modelimport android.view.Viewimport java.io.Serializableclass MultiStyleItem:Serializable{    enum class Options    {        TITLE,MULTI_BUTTON,MULTI_RADIO_BUTTON,MULTI_CHECKBOX,MULTI_HYBRID,SELECT_DIALOG,TWO_OPTIONS_SELECT_DIALOG,THREE_OPTIONS_SELECT_DIALOG,SINGLE_INPUT,INPUT_RANGE,TWO_PAIR_INPUT,INPUT_WITH_UNIT,INPUT_WITH_MULTI_UNIT,INPUT_WITH_TEXTAREA,SHIFT_INPUT,HINT,SUBMIT,EXPAND,SINGLE_DISPLAY    }    var options=Options.TITLE    lateinit var key:String    var necessary:Boolean=false    lateinit var title1:String    var backListener:View.OnClickListener?=null    //wheelView    lateinit var selectTitle:String    lateinit var selectOption1Items:List<String>    lateinit var selectOption2Items:List<List<String>>    lateinit var selectOption3Items:List<List<List<String>>>    var selectListener:View.OnClickListener?=null    //multi button    lateinit var buttonTitle:String    lateinit var buttonName:List<String>    lateinit var buttonListener:List<View.OnClickListener>    //input with unit    lateinit var inputUnitTitle:String    lateinit var inputUnit:String    //two pair input    lateinit var twoPairInputTitle:String    lateinit var twoPairInputItem1:String    lateinit var twoPairInputItem2:String    lateinit var twoPairInputItem3:String    //input with multi unit    lateinit var inputMultiUnitTitle:String    lateinit var inputMultiUnit:List<String>    //single input    lateinit var inputSingleTitle:String    //input range    lateinit var inputRangeTitle:String    lateinit var inputRangeUnit:String    //multi radiobutton    lateinit var radioButtonTitle:String    lateinit var radioButtonName:List<String>    //multi checkbox    lateinit var checkboxNameList:List<String>    lateinit var checkboxTitle:String    //input with textarea    lateinit var textAreaTitle:String    //hint    lateinit var hintContent:String    var customFontColor:String?=null    var startPosition:Int?=null    var endPosition:Int?=null    /*    object customTheme{        var custormFontColor:String?=null        var startPosition:Int?=null        var endPosition:Int?=null    }    var customThemeList:List<customTheme> =ArrayList()*/    //submit    lateinit var submitContent:String    var submitListener: View.OnClickListener?=null    //multi hybrid contain button radiobutton    lateinit var hybridTitle:String    lateinit var hybridButtonName: List<String>    lateinit var hybridRadioButtonName:List<String>    lateinit var hybridCheckBoxName:List<String>    lateinit var hybridButtonListeners:List<View.OnClickListener>    //expand 可展开的子项    lateinit var expandTitle:String    lateinit var expandContent:String    //shiftInput 跳转到新界面去输入    lateinit var shiftInputTitle:String    lateinit var shiftinputContent:String    var jumpListener:View.OnClickListener?=null    //single display    lateinit var singleDisplayTitle:String    lateinit var singleDisplayContent:String    constructor(options: Options, title1: String, title2: String) {        this.options = options        when(options)        {            Options.TITLE->            {                this.title1 = title1            }            Options.INPUT_WITH_UNIT->            {                this.inputUnitTitle=title1                this.inputUnit=title2            }            Options.SINGLE_DISPLAY->            {                this.singleDisplayTitle=title1                this.singleDisplayContent=title2            }            Options.INPUT_RANGE->            {                this.inputRangeTitle=title1                this.inputRangeUnit=title2            }            Options.EXPAND->            {                this.expandTitle=title1                this.expandContent=title2            }            Options.SHIFT_INPUT->{                this.shiftInputTitle=title1                this.shiftinputContent=title2            }        }    }    constructor(options: Options,selectOption1Items:List<String>,selectOption2Items: List<List<String>>,selectOption3Items:List<List<List<String>>>,selectTitle: String)    {        this.options=options        when(options)        {            Options.TWO_OPTIONS_SELECT_DIALOG->            {                this.selectOption1Items=selectOption1Items                this.selectOption2Items=selectOption2Items                this.selectTitle=selectTitle            }            Options.THREE_OPTIONS_SELECT_DIALOG->            {                this.selectOption1Items=selectOption1Items                this.selectOption2Items=selectOption2Items                this.selectOption3Items=selectOption3Items                this.selectTitle=selectTitle            }        }    }    constructor(options: Options, selectNameList: List<String>, selectTitle: String) {        this.options = options        when(options)        {            Options.SELECT_DIALOG->            {                this.selectOption1Items = selectNameList                this.selectTitle = selectTitle            }            Options.MULTI_CHECKBOX->            {                this.checkboxNameList=selectNameList                this.checkboxTitle=selectTitle            }            Options.MULTI_RADIO_BUTTON->            {                this.radioButtonName=selectNameList                this.radioButtonTitle=selectTitle            }            Options.INPUT_WITH_MULTI_UNIT->            {                this.inputMultiUnitTitle=selectTitle                this.inputMultiUnit=selectNameList            }        }    }    constructor(        options: Options,        buttonTitle: String,        buttonName: List<String>,        buttonListener: List<View.OnClickListener>    ) {        this.options = options        this.buttonTitle = buttonTitle        this.buttonName = buttonName        this.buttonListener = buttonListener    }    constructor(options: Options, inputSingleTitle: String) {        this.options = options        when(options)        {            Options.TITLE->            {                this.title1=inputSingleTitle            }            Options.HINT->            {                this.hintContent=inputSingleTitle            }            Options.SUBMIT->            {                this.submitContent=inputSingleTitle            }            Options.INPUT_WITH_TEXTAREA->            {                this.textAreaTitle=inputSingleTitle            }            Options.SINGLE_INPUT->            {                this.inputSingleTitle=inputSingleTitle            }        }    }    constructor(options: Options, inputSingleTitle: String,necessary:Boolean)    {        this.options=options        when(options)        {            Options.SHIFT_INPUT->            {                this.necessary=necessary                this.shiftInputTitle=inputSingleTitle            }        }    }    constructor()    {    }    constructor(        options: Options,        hybridTitle: String,        hybridButtonName: List<String>,        hybridRadioButtonName: List<String>,        hybridCheckBoxName: List<String>,        hybridButtonListeners: List<View.OnClickListener>    ) {        this.options = options        this.hybridTitle = hybridTitle        this.hybridButtonName = hybridButtonName        this.hybridRadioButtonName = hybridRadioButtonName        this.hybridCheckBoxName = hybridCheckBoxName        this.hybridButtonListeners = hybridButtonListeners    }    constructor(        options: Options,        twoPairInputTitle: String,        twoPairInputItem1: String,        twoPairInputItem2: String,        twoPairInputItem3: String    ) {        this.options = options        this.twoPairInputTitle = twoPairInputTitle        this.twoPairInputItem1 = twoPairInputItem1        this.twoPairInputItem2 = twoPairInputItem2        this.twoPairInputItem3 = twoPairInputItem3    }}