package com.electric.engineering.modelimport android.view.Viewclass MultiStyleItem{    enum class Options    {        TITLE,MULTI_BUTTON,MULTI_RADIO_BUTTON,MULTI_CHECKBOX,MULTI_HYBRID,SELECT_DIALOG,TWO_OPTIONS_SELECT_DIALOG,THREE_OPTIONS_SELECT_DIALOG,SINGLE_INPUT,INPUT_RANGE,INPUT_WITH_UNIT,INPUT_WITH_MULTI_UNIT,INPUT_WITH_TEXTAREA,HINT,SUBMIT,EXPAND    }    var options=Options.TITLE    lateinit var title1:String    //wheelView    lateinit var selectTitle:String    lateinit var selectOption1Items:List<String>    lateinit var selectOption2Items:List<List<String>>    lateinit var selectOption3Items:List<List<List<String>>>    //multi button    lateinit var buttonTitle:String    lateinit var buttonName:List<String>    lateinit var buttonListener:List<View.OnClickListener>    //input with unit    lateinit var inputUnitTitle:String    lateinit var inputUnit:String    //input with multi unit    lateinit var inputMultiUnitTitle:String    lateinit var inputMultiUnit:List<String>    //single input    lateinit var inputSingleTitle:String    //input range    lateinit var inputRangeTitle:String    lateinit var inputRangeUnit:String    //multi radiobutton    lateinit var radioButtonTitle:String    lateinit var radioButtonName:List<String>    //multi checkbox    lateinit var checkboxNameList:List<String>    lateinit var checkboxTitle:String    //input with textarea    lateinit var textAreaTitle:String    //hint    lateinit var hintContent:String    //submit    lateinit var submitContent:String    lateinit var submitListener: View.OnClickListener    //multi hybrid contain button radiobutton    lateinit var hybridTitle:String    lateinit var hybridButtonName: List<String>    lateinit var hybridRadioButtonName:List<String>    lateinit var hybridCheckBoxName:List<String>    lateinit var hybridButtonListeners:List<View.OnClickListener>    //expand 可展开的子项    lateinit var expandTitle:String    lateinit var expandContent:String    constructor(options: Options, title1: String, title2: String) {        this.options = options        when(options)        {            Options.TITLE->            {                this.title1 = title1            }            Options.INPUT_WITH_UNIT->            {                this.inputUnitTitle=title1                this.inputUnit=title2            }            Options.INPUT_RANGE->            {                this.inputRangeTitle=title1                this.inputRangeUnit=title2            }            Options.EXPAND->            {                this.expandTitle=title1                this.expandContent=title2            }        }    }    constructor(options: Options,selectOption1Items:List<String>,selectOption2Items: List<List<String>>,selectOption3Items:List<List<List<String>>>,selectTitle: String)    {        this.options=options        when(options)        {            Options.TWO_OPTIONS_SELECT_DIALOG->            {                this.selectOption1Items=selectOption1Items                this.selectOption2Items=selectOption2Items                this.selectTitle=selectTitle            }            Options.THREE_OPTIONS_SELECT_DIALOG->            {                this.selectOption1Items=selectOption1Items                this.selectOption2Items=selectOption2Items                this.selectOption3Items=selectOption3Items                this.selectTitle=selectTitle            }        }    }    constructor(options: Options, selectNameList: List<String>, selectTitle: String) {        this.options = options        when(options)        {            Options.SELECT_DIALOG->            {                this.selectOption1Items = selectNameList                this.selectTitle = selectTitle            }            Options.MULTI_CHECKBOX->            {                this.checkboxNameList=selectNameList                this.checkboxTitle=selectTitle            }            Options.MULTI_RADIO_BUTTON->            {                this.radioButtonName=selectNameList                this.radioButtonTitle=selectTitle            }            Options.INPUT_WITH_MULTI_UNIT->            {                this.inputMultiUnitTitle=selectTitle                this.inputMultiUnit=selectNameList            }        }    }    constructor(        options: Options,        buttonTitle: String,        buttonName: List<String>,        buttonListener: List<View.OnClickListener>    ) {        this.options = options        this.buttonTitle = buttonTitle        this.buttonName = buttonName        this.buttonListener = buttonListener    }    constructor(options: Options, inputSingleTitle: String) {        this.options = options        when(options)        {            Options.TITLE->            {                this.title1=inputSingleTitle            }            Options.HINT->            {                this.hintContent=inputSingleTitle            }            Options.SINGLE_INPUT->            {                this.inputSingleTitle = inputSingleTitle            }            Options.SUBMIT->            {                this.submitContent=inputSingleTitle            }            Options.INPUT_WITH_TEXTAREA->            {                this.textAreaTitle=inputSingleTitle            }        }    }    constructor()    {    }    constructor(        options: Options,        hybridTitle: String,        hybridButtonName: List<String>,        hybridRadioButtonName: List<String>,        hybridCheckBoxName: List<String>,        hybridButtonListeners: List<View.OnClickListener>    ) {        this.options = options        this.hybridTitle = hybridTitle        this.hybridButtonName = hybridButtonName        this.hybridRadioButtonName = hybridRadioButtonName        this.hybridCheckBoxName = hybridCheckBoxName        this.hybridButtonListeners = hybridButtonListeners    }}